mapper.delete:
delete from wish 
where member_num = #{memberNum} 
and goods_num in 

		#{goodsNum}

delete from cart
where member_num = #{memberNum} 
and goods_num in 
	#{goodsNum}

mapper.update:
update cart
set cart_qty = cart_qty - 1
where goods_num = #{arg0} and member_num = #{arg1}

mapper.select:
select goods_price , cart_qty
from goods g , cart c
where g.goods_num = c.goods_num and goods_num = #{arg0} and member_num = #{arg1}
