mapper.select:
select *
from  ( select ROW_NUMBER() over (order by member_num) as rn,
			
		from members
		
		where MEMBER_NAME like '%' || #{searchWord} || '%'
		   or member_id like '%' || #{searchWord} || '%'
		   or member_num like '%' || #{searchWord} || '%'
		
	   ) sub1
where rn between #{startRow}  and #{endRow}

mapper.delete:
delete from members
where member_num in
	
		#{memberNum}
